operation.add:
//=========== OpenCart Shortcodes

//=== Default shortcode
$this->load->library('shortcodes');

$class      = new Shortcodes($this->registry);
$shortcodes = get_class_methods('shortcodes');
$shortcodes = array_slice($shortcodes, 3);
foreach ($shortcodes as $shortcode) {
   add_shortcode($shortcode, $shortcode, $class);
}

//=== Theme extending shortcode
$file = DIR_TEMPLATE . $this->config->get('config_template') . '/shortcodes.php';
if (file_exists($file)) {
   include_once($file);

   $class      = new themeShortcodes($this->registry);
   $shortcodes = get_class_methods('shortcodes');
   foreach ($shortcodes as $shortcode) {
      add_shortcode($shortcode, $shortcode, $class);
   }
}

//=========== END :: OpenCart Shortcodes

			//=========== OpenCart Shortcodes
         if (isset($this->session->data['shortcodes'])) {
            $shortcodes = $this->session->data['shortcodes'];

            isset($shortcodes['setTitle']) ? $this->document->setTitle($shortcodes['setTitle']) : '' ;
            isset($shortcodes['setDescription']) ? $this->document->setDescription($shortcodes['setDescription']) : '' ;
            isset($shortcodes['setKeywords']) ? $this->document->setKeywords($shortcodes['setKeywords']) : '' ;

            if (isset($shortcodes['addStyle']) && is_array($shortcodes['addStyle'])) {
               foreach ($shortcodes['addStyle'] as $style) {
                  $this->document->addStyle($style);
               }
            }
            //$this->document->addScript( $script )

            unset($this->session->data['shortcodes']);
         }
         //=========== END :: OpenCart Shortcodes
